import React from "react";
import { Col } from "shards-react";
import { Formik, Field, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import { ToastContainer, toast } from 'react-toastify';
import { orderService } from "../../redux/services/order.service";


const AddEmailsForm = () => (
  <div>
    <ToastContainer/>
    <Formik
      initialValues={{
        email: '',
      }}
      validationSchema={Yup.object().shape({
        email: Yup.string().required('Cannot be empty'),
      })}
      onSubmit={({ email }, { setStatus, setSubmitting }) => {
        
        orderService.addEmailAssets({ email })
          .then((res) => {
            setSubmitting(false);
            toast.success(res);
          })
          .catch((e) => {
            setSubmitting(false);
            setStatus(e);
            toast.error("Try again later");
          })
      }}
      render={({ errors, status, touched, isSubmitting }) => (
        <Col>
          <Form>
            <div className="form-group">
              <label htmlFor="email"></label>
              <Field name="email" type="text" className={'form-control' + (errors.email && touched.email ? ' is-invalid' : '')} />
              <ErrorMessage name="email" component="div" className="invalid-feedback" />
            </div>
            <div className="form-group">
              <button type="submit" className="btn btn-primary" disabled={isSubmitting}>Add</button>
              {isSubmitting &&
                <img alt="" src="data:image/gif;base64," />
              }
            </div>
          </Form>
        </Col>
      )}
    />
  </div>

);

export default AddEmailsForm;
